memberdef.initializer:
= requires(TMatrix M)
{
    typename TMatrix::ScalarType;
    {
        TMatrix::kRows
    } -> std::convertible_to<int>;
    {
        TMatrix::kCols
    } -> std::convertible_to<int>;
    {
        TMatrix::bRowMajor
    } -> std::convertible_to<bool>;















}

= CMatrix<TMatrix> && requires(TMatrix M)
{
    {
        M.Data()
    } -> std::convertible_to<typename TMatrix::ScalarType*>;
}
